import{_ as t,o as a,c as i,ag as n}from"./chunks/framework.DEqXEGcv.js";const p=JSON.parse('{"title":"Token Gating","description":"","frontmatter":{},"headers":[],"relativePath":"zenon/token-gating.md","filePath":"zenon/token-gating.md"}'),o={name:"zenon/token-gating.md"};function s(l,e,r,d,h,c){return a(),i("div",null,[...e[0]||(e[0]=[n(`<h1 id="token-gating" tabindex="-1">Token Gating <a class="header-anchor" href="#token-gating" aria-label="Permalink to &quot;Token Gating&quot;">​</a></h1><p>Token gating restricts group membership to users who hold a minimum balance of a specific ZTS token. When enabled, new joiners without a linked wallet or with insufficient balance are automatically kicked.</p><h2 id="setup" tabindex="-1">Setup <a class="header-anchor" href="#setup" aria-label="Permalink to &quot;Setup&quot;">​</a></h2><h3 id="_1-enable-token-gating" tabindex="-1">1. Enable token gating <a class="header-anchor" href="#_1-enable-token-gating" aria-label="Permalink to &quot;1. Enable token gating&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/zgate enable zts1znnxxxxxxxxxxxxx9z4ulx 100</span></span></code></pre></div><p>This requires all members to hold at least 100 ZNN. You can use any ZTS token address.</p><h3 id="_2-members-link-their-wallets" tabindex="-1">2. Members link their wallets <a class="header-anchor" href="#_2-members-link-their-wallets" aria-label="Permalink to &quot;2. Members link their wallets&quot;">​</a></h3><p>Members need to link their Zenon address before joining (or within the grace period):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/zwallet z1qph8...</span></span>
<span class="line"><span>/zwallet verify</span></span></code></pre></div><h3 id="_3-check-eligibility" tabindex="-1">3. Check eligibility <a class="header-anchor" href="#_3-check-eligibility" aria-label="Permalink to &quot;3. Check eligibility&quot;">​</a></h3><p>Members can verify their status:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/zgate check</span></span></code></pre></div><h2 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h2><p>When a new user joins the group:</p><ol><li>The bot checks if they have a linked and verified Zenon wallet</li><li>If no wallet is linked, the user is kicked with a message explaining they need to link one</li><li>If the wallet balance is below the minimum, the user is kicked with a message showing the requirement</li><li>If the balance meets the requirement, the user is allowed to stay</li></ol><h2 id="commands" tabindex="-1">Commands <a class="header-anchor" href="#commands" aria-label="Permalink to &quot;Commands&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Command</th><th>Permission</th><th>Description</th></tr></thead><tbody><tr><td><code>/zgate info</code></td><td>Everyone</td><td>Show current gating settings</td></tr><tr><td><code>/zgate enable &lt;zts&gt; &lt;min&gt;</code></td><td>Admin</td><td>Enable gating with a token and minimum balance</td></tr><tr><td><code>/zgate disable</code></td><td>Admin</td><td>Disable token gating</td></tr><tr><td><code>/zgate check</code></td><td>Everyone</td><td>Check your own eligibility</td></tr></tbody></table><h2 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h2><ul><li>Token gating only checks at join time. Existing members are not retroactively removed if their balance drops.</li><li>The bot needs a working Zenon node connection to verify balances.</li><li>Users must have a linked wallet <strong>before</strong> joining the group.</li></ul>`,19)])])}const b=t(o,[["render",s]]);export{p as __pageData,b as default};
